    private void saveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_saveActionPerformed
        // TODO add your handling code here:
        String sql = "INSERT INTO feedback_layanan VALUES (?,?,?,?,?,?)";
try {
    PreparedStatement stat = conn.prepareStatement(sql);
    stat.setString(1, id.getText());
    stat.setString(2, idlaporan.getText());
    stat.setString(3, nama.getText());
    stat.setString(4, petugas.getSelectedItem().toString());
    stat.setString(5, keterangan.getText());
    stat.setString(6, saran.getText());

    stat.executeUpdate();
    JOptionPane.showMessageDialog(null, "Data akan disimpan");

    // Update status in the "layanan" table
    String updateStatusSql = "UPDATE layanan SET status = true WHERE id = ?";
    PreparedStatement updateStat = conn.prepareStatement(updateStatusSql);
    updateStat.setString(1, idlaporan.getText());
    updateStat.executeUpdate();

    kosong();
    id.requestFocus();
    tabel();
    kode();
} catch (SQLException e) {
    JOptionPane.showMessageDialog(null, "Data gagal disimpan" + e);
    tabel();
}
    }//GEN-LAST:event_saveActionPerformed

