    private void petugasItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_petugasItemStateChanged
        // TODO add your handling code here:
        String[] opsii = petugas.getSelectedItem().toString().split("//s+");
        String kode = opsii[0];
        if(!kode.equals("PETUGAS")){
            try {
                Class.forName("com.mysql.jdbc.Driver");
                conn=DriverManager.getConnection("jdbc:mysql://localhost/pengaduanapp","root","");
                stm=conn.createStatement();
                String namas = "";
                String opsiii = petugas.getSelectedItem().toString();
                switch(petugas.getSelectedIndex()){
                    case 1: namas = "1"; break;
                    case 2: namas = "2"; break;
                    case 3: namas = "3"; break;
                    case 4: namas = "4"; break;
                    case 5: namas = "5"; break;
                    case 6: namas = "6"; break;
                    case 7: namas = "7"; break;
                    case 8: namas = "8"; break;
                    case 9: namas = "9"; break;
                    case 10: namas = "10"; break;
                    case 11: namas = "11"; break;
                    case 12: namas = "12"; break;
                }
                java.sql.Statement stm = conn.createStatement();
                java.sql.ResultSet rs = stm.executeQuery("SELECT * FROM petugas WHERE nama_petugas='"+opsiii+"'");
                if(rs.next()){;
                    idpetugas.setText(rs.getString("id_petugas"));
                }

            } catch (SQLException e) {
                JOptionPane.showMessageDialog(null, "Error " + e);
            } catch (ClassNotFoundException ex) {
                Logger.getLogger(mastersiswa.class.getName()).log(Level.SEVERE, null, ex);
            }
        }else{
        }
    }//GEN-LAST:event_petugasItemStateChanged
public Connection setKoneksi(){
        try {
            Class.forName("com.mysql.jdbc.Driver");
            conn=DriverManager.getConnection("jdbc:mysql://localhost/pengaduanapp","root","");
            stm=conn.createStatement();
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null,"Koneksi Gagal :" +e);
        }
       return conn; 
    }
    
    public void tabel(){
        Object header[]={"ID LAPORAN","NAMA SISWA","LAPORAN"};
        DefaultTableModel data=new DefaultTableModel(null,header);
        tabel.setModel(data);
        setKoneksi();
        String sql="SELECT * FROM layanan where status ='0'";
        try {
            ResultSet rs=stm.executeQuery(sql);
            while (rs.next())
            {
                String kolom1=rs.getString("id");
                String kolom2=rs.getString("namasiswa");
                String kolom3=rs.getString("keterangan");

                String kolom[]={kolom1,kolom2,kolom3};
                data.addRow(kolom);
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "data gagal dipanggil"+e);
        }
    }
